import React, { useEffect, useState } from 'react';
import ArrowBack from '../ArrowBack/ArrowBack';
import './Form.css';

const Form = ({ onSubmit, id }) => {
  const [productsData, setProductsData] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(
          'http://localhost:3001/api/products//sale-order',
        );
        const data = await response.json();

        if (id) {
          // If an `id` is provided, find the related product
          const product = data.find((p) => p.id === id);
          setSelectedProduct(product ? [product] : []); // Set only the related product
        } else {
          // If no `id` is provided, show all products
          setProductsData(data);
        }
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, [id]);

  const productsToDisplay =
    id && selectedProduct ? selectedProduct : productsData;

  return (
    <>
      <div className="arrow-back-container-form">
        <ArrowBack />
      </div>
      <form onSubmit={onSubmit} id="form-sale-order">
        <label htmlFor="name">Nome</label>
        <input type="text" id="name" name="name" />
        <label htmlFor="table">Mesa</label>
        <input type="text" id="table" name="table" />
        <label htmlFor="saleOrder">Pedido</label>
        <select id="product" name="product">
