import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import ArrowBack from '../ArrowBack/ArrowBack';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './DisplayData.css';

const DisplayData = ({ formData }) => {
  const { id } = useParams(); // Capture the 'id' from the URL
  const history = useNavigate();
  const [product, setProduct] = useState(null); // State to store product data

  useEffect(() => {
    // Fetch product data from the back-end
    const fetchProduct = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/products/${id}`,
        ); // Replace with your API endpoint
        if (!response.ok) {
          throw new Error('Failed to fetch product data');
        }
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error(error);
        toast.error('Erro ao carregar os dados do produto.');
      }
    };

    if (id) {
      fetchProduct();
    }
  }, [id]);

  if (!formData || !product) return null;

  const handleFinalizeClick = () => {
    toast.success('Seu pedido está chegando!', {
      position: 'top-center',
      closeButton: false,
      autoClose: 3000, // Fecha automaticamente após 3 segundos
    });
    history('/');
  };

  return (
    <>
      <div className="arrow-back-container-form">
        <ArrowBack />
      </div>
      <div className="display-data" id="display-data">
        <div className="data-orders">
          <strong>Nome:</strong>
          <div className="data-orders-values">{formData.name}</div>
        </div>
        <div className="data-orders">
          <strong>Mesa:</strong>
          <div className="data-orders-values">{formData.table}</div>
        </div>
        <div className="data-order-prices">
          <div className="order">
            <div className="data-orders">
              <strong>Pedido:</strong>
              <div className="data-orders-values">{product.name}</div>
            </div>
          </div>
          <div className="price">
            <div className="data-orders">
              <strong>Valor: </strong>
              <div className="data-orders-values">
                R$ {product.price.toFixed(2).replace('.', ',')}
              </div>
            </div>
          </div>
        </div>
        <button type="button" onClick={handleFinalizeClick}>
          Finalizar
        </button>
      </div>
    </>
  );
};

export default DisplayData;
