import React from 'react';
import axios from 'axios';

const FormProduct = () => {
  const handleSubmit = async (event) => {
    event.preventDefault();
    const nameProduct = event.target.nameProduct.value;
    const imageProduct = event.target.imageProduct.files[0];
    const slugProduct = event.target.slugProduct.value;
    const priceProduct = event.target.priceProduct.value;

    const formData = new FormData();
    formData.append('name', nameProduct);
    formData.append('imageUrl', imageProduct);
    formData.append('slug', slugProduct);
    formData.append('price', priceProduct);

    try {
      await axios.post('http://localhost:3000/api/products/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert('Produto cadastrado com sucesso!');
    } catch (error) {
      alert('Erro ao cadastrar produto!');
      console.error('Erro ao cadastrar produto:', error);
    }
  };

  return (
    <div class="form-product-container">
      <div class="form-product-header">
        <h3>Formulário de Produto</h3>
      </div>
      <form class="formProduct" onSubmit={handleSubmit}>
        <div class="inputs-product-container">
          <label for="nameProduct">Nome do Produto</label>
          <input
            type="text"
            name="nameProduct"
            id="nameProduct"
            placeholder="Nome do produto"
          />
          <label for="imageProduct">Imagem para o Produto</label>
          <input
            type="file"
            name="imageProduct"
            id="imageProduct"
            placeholder="Imagem para o produto"
          />
          <label for="slugProduct">Link para o Produto</label>
          <input
            type="text"
            name="slugProduct"
            id="slugProduct"
            placeholder="Slug-do-produto"
          />
          <label for="priceProduct">Preço do Produto</label>
          <input
            type="text"
            name="priceProduct"
            id="priceProduct"
            placeholder="Preço do produto"
          />
          <input type="submit" value="Enviar" />
        </div>
      </form>
    </div>
  );
};

export default FormProduct;
